import './App.css';
import React from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import NavBar from './components/NavBar';
import UploadActivities from './components/UploadActivities';
import Stats from './components/Stats';
import ActivityHistory from './components/ActivityHistory';

function App() {
  // Inline style for dynamic background image
  const backgroundStyle = {
    backgroundImage: 'url(/uphillRun.jpg)', // Path to your image in the public folder
    backgroundSize: 'cover', // Makes sure the image covers the full screen
    backgroundPosition: 'center', // Centers the image
    height: '100vh', // Makes the background cover the full height of the screen
    display: 'flex', // Centers content vertically and horizontally
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
  };

  return (
    <Router>
      <div style={backgroundStyle}>
        {/* Navigation and content */}
        <NavBar />
        <h1 style={{ color: '#FFF' }}>Welcome to RunningOnData</h1>
        <nav>
          <Link to="/" style={{ color: 'white', margin: '10px' }}>Home</Link>
          <Link to="/upload" style={{ color: 'white', margin: '10px' }}>Upload</Link>
          <Link to="/stats" style={{ color: 'white', margin: '10px' }}>Stats</Link>
          <Link to="/history" style={{ color: 'white', margin: '10px' }}>History</Link>
        </nav>

        {/* Routes */}
        <Routes>
          <Route path="/upload" element={<UploadActivities />} />
          <Route path="/stats" element={<Stats />} />
          <Route path="/history" element={<ActivityHistory />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
