import './App.css';
import React from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import NavBar from './components/NavBar';
import HomeLink from './components/HomeLink';
import UploadActivities from './components/UploadActivities';
import Stats from './components/Stats';
import ActivityHistory from './components/ActivityHistory';

function App() {
  return (
    <Router>
      <NavBar />

      {/* Main Dashboard Banner Image */}
      <div style={{
        position: 'relative',
        height: '100vh',  // Make sure it takes full viewport height
        overflow: 'hidden',  // Ensure no overflow at the bottom
      }}>
        <img 
          src="/images/uphillRun.jpg" // Replace with your image URL
          alt="Running Banner"
          style={{
            width: '100%', // Make the image take full width
            height: '100%', // Make the image take full height
            objectFit: 'cover', // Ensure it covers the container area
            objectPosition: 'center', // Keep the image centered
            position: 'absolute', // Position it absolutely within the container
            top: 0,
            left: 0,
          }}
        />
        <div style={{
          position: 'absolute', // Positioning the text above the image
          top: '25%', // Vertically centering the text
          left: '50%',
          transform: 'translate(-50%, -50%)',
          color: '#FFF',
          textAlign: 'center',
          zIndex: 1, // Make sure text appears above the image
        }}>
          <h1>RunningOnData Dashboard</h1>
        </div>
      </div>

      <nav style={{
        position: 'absolute', // Position navigation below the image
        bottom: '20px', // Adjust the bottom margin
        left: '50%',
        transform: 'translateX(-50%)',
        zIndex: 1, // Make sure nav is on top of the image
        textAlign: 'center', // Center the navigation links
      }}>
        <Link to="/" style={{ color: 'white', margin: '10px' }}>Home</Link>
        <Link to="/upload" style={{ color: 'white', margin: '10px' }}>Upload</Link>
        <Link to="/stats" style={{ color: 'white', margin: '10px' }}>Stats</Link>
        <Link to="/history" style={{ color: 'white', margin: '10px' }}>History</Link>
      </nav>

      {/* Define Routes */}
      <Routes>
        <Route path="/upload" element={<UploadActivities />} />
        <Route path="/stats" element={<Stats />} />
        <Route path="/history" element={<ActivityHistory />} />
      </Routes>
    </Router>
  );
}

export default App;
